import React, { useEffect } from "react";

function ImagePopup({ card, onClose }) {
  // Функция handleOverlayClick вызывает функцию onClose, если клик произошел на затемненной области вокруг модального окна
  const handleOverlayClick = (e) => {
    if (e.target.classList.contains("popup_is-opened")) {
      onClose();
    }
  };

  // Используем useEffect для добавления и удаления обработчика событий нажатия клавиши Escape
  useEffect(() => {
    const handleEscClose = (e) => {
      if (e.key === "Escape") {
        onClose();
      }
    };

    // Если объект card существует, добавляем обработчик события keydown для закрытия модального окна по клавише Esc
    if (card) {
      document.addEventListener("keydown", handleEscClose);
    }
  }, [card, onClose]);

  return (
    <section
      className={`popup ${card ? "popup_is-opened" : ""}`}
      id="image-popup"
      onClick={handleOverlayClick}
    >
      <figure className="popup__image-container">
        <img className="popup__image" src={card?.link} alt={card?.name} />
        <figcaption className="popup__caption">{card?.name}</figcaption>
        <button
          type="button"
          className="popup__close"
          onClick={onClose}
        ></button>
      </figure>
    </section>
  );
}

export default ImagePopup;
