import {useEffect} from 'react';

function PopupWithForm(props) {
  // Закрытие попапа по клику по иконке закрытия
  const handleCloseButtonClick = (event) => {
    if (event.target.classList.contains('popup__close')) {
      props.onClose
    }
  }

}
