// Импортируем React из библиотеки 'react'
import React from "react";

// Создаем функциональный компонент Card, который принимает объект card и функцию onCardClick
function Card({ card, onCardClick }) {
  // Функция handleClick вызывает функцию onCardClick, передавая ей объект card
  const handleClick = () => {
    onCardClick(card);
  };

  // Возвращаем JSX разметку для карточки
  return (
    <article className="element">
      {/* Отображаем изображение карточки, при клике на которое вызывается функция handleClick */}
      <img
        className="element__image"
        src={card.link}
        alt={card.name}
        onClick={handleClick}
      />

      <button
        type="button"
        className="element__trash"
        aria-label="Удалить"
      ></button>
      <div className="element__wrapper">
        <h2 className="element___name">{card.name}</h2>
        <div>
          <button
            type="button"
            className="element__like"
            aria-label="Лайк"
          ></button>
          <p className="element__like-count">{card.likes.length}</p>
        </div>
      </div>
    </article>
  );
}

export default Card;
