import React from "react";

function Main({ onEditAvatar, onEditProfile, onAddPlace}) {
  const handleEditAvatarClick = () => {
    document.querySelector("#avatar-popup").classList.add("popup_is-opened");
  };

  const handleEditProfileClick = () => {
    document.querySelector("#edit-popup").classList.add("popup_is-opened");
  };

  const handleAddPlaceClick = () => {
    document.querySelector("#add-popup").classList.add("popup_is-opened");
  };

  return (
    <main className="content">
      {/* profile */}
      <section className="profile">
        <div className="profile__wrapper">
          <div className="profile__avatar-wrapper">
            <img className="profile__avatar" />
            <button
              type="button"
              className="profile__avatar-edit-button"
              onClick={handleEditAvatarClick}
            ></button>
          </div>
          <div className="profile__info">
            <div className="profile__inner-wrapper">
              <h1 className="profile__name"></h1>
              <button
                type="button"
                className="profile__edit-button"
                onClick={handleEditProfileClick}
              ></button>
            </div>
            <p className="profile__about"></p>
          </div>
        </div>
        <button
          type="button"
          className="profile__add-button"
          onClick={handleAddPlaceClick}
        ></button>
      </section>
      {/* elements */}
      <section className="elements"></section>
    </main>
  );
}

export default Main;
